pragma solidity ^0.6.0;


contract PubKeyRegistry
{
	mapping(address => bytes) public pubkeys;

	event PubKey(address, bytes);

	function _pubKeyToAddr(bytes memory pubkey) internal pure returns (address)
	{
		require(pubkey.length == 64);
		return address(uint256(keccak256(abi.encodePacked(pubkey))));
	}

	function addEthPubKey(bytes calldata pubkey) external
	{
			address addr = _pubKeyToAddr(pubkey);
			require(pubkeys[addr].length == 0);
			pubkeys[addr] = pubkey;
			emit PubKey(addr, pubkey);
	}

	function setPubKey(bytes calldata pubkey) external
	{
			pubkeys[msg.sender] = pubkey;
			emit PubKey(msg.sender, pubkey);
	}
}

